    "    \n",
    "    var level = parseFloat(wfreq) * 20;\n",
    "  \n",
    "    \n",
    "    var degrees = 180 - level;\n",
    "    var radius = 0.5;\n",
    "    var radians = (degrees * Math.PI) / 180;\n",
    "    var x = radius * Math.cos(radians);\n",
    "    var y = radius * Math.sin(radians);\n",
    "  \n",
    "    \n",
    "    var mainPath = \"M -.0 -0.05 L .0 0.05 L \";\n",
    "    var pathX = String(x);\n",
    "    var space = \" \";\n",
    "    var pathY = String(y);\n",
    "    var pathEnd = \" Z\";\n",
